import { useEffect, useState } from 'react';
import axios from 'axios';
import './App.css';

const API_URL = 'http://localhost:8000.com/';
const emailRegex = /^(([^<>()[\]\\.,;:\s@"]+(\.[^<>()[\]\\.,;:\s@"]+)*)|(".+"))@((\[[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\])|(([a-zA-Z\-0-9]+\.)+[a-zA-Z]{2,3}))$/;

function App() {
  const [inLogin, setInLogin] = useState(true);
  const [nickName, setNickName] = useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [invalid, setInvalid] = useState(false);

  async function handleSubmit() {
    setInvalid(false);
    if (inLogin) {
      if (!email || !password) {
        setInvalid(true);
        return;
      }
      const url = `${API_URL}users/api/log-in/`;
      let data = {};
      if (emailRegex.test(email)) {
        data = {
          "email": email,
          "password": password,
        };
      } else {
        data = {
          "nick_name": email,
          "password": password,
        };
      }
      console.log('Here');
      const user = await axios({
        method: 'POST',
        url,
        data,
      })
        .then((res) => res.data)
        .then((res) => {
          console.log(res);
        });
    } else {
      if (!nickName || !password || !email) {
        setInvalid(true);
        return;
      }
      const url = `${API_URL}users/api/sign-up/`;
      const data = {
        "email": email,
        "password": password,
        "nick_name": email,
      };
      const user = await axios({
        method: 'POST',
        url,
        data,
      })
        .then((res) => res.data)
        .then((res) => {
          console.log(res);
        });
    }
  }

  useEffect(() => {
    setInvalid(false);
  }, [inLogin])

  return (
    <div className="App">
      {(inLogin)
        ? (
          <div className="container">
            <p className="title">
              Log In
            </p>
            <input
              type="text"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="email"
            />
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="contraseña"
            />
            <button
              type="button"
              className="confirm-button"
              onClick={() => handleSubmit()}
            >
              Confirmar
            </button>
            <button
              type="button"
              className="none-account"
              onClick={() => setInLogin(false)}
            >
              Crear cuenta
            </button>
            {(invalid) && (
              <p className="invalid">
                Request incorrecto. Debe llenar todos los campos.
              </p>
            )}
          </div>
        ) : (
          <div className="container">
            <p className="title">
              Sign Up
            </p>
            <input
              type="text"
              value={nickName}
              onChange={(e) => setNickName(e.target.value)}
              placeholder="username"
            />
            <input
              type="text"
              value={email}
              onChange={(e) => setEmail(e.target.value)}
              placeholder="email"
            />
            <input
              type="password"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              placeholder="contraseña"
            />
            <button
              type="button"
              className="confirm-button"
              onClick={() => handleSubmit()}
            >
              Confirmar
            </button>
            <button
              type="button"
              className="none-account"
              onClick={() => setInLogin(true)}
            >
              Log in
            </button>
            {(invalid) && (
              <p className="invalid">
                Request incorrecto. Debe llenar todos los campos.
              </p>
            )}
          </div>
        )}
    </div>
  );
}

export default App;
